exports.init = function (runtime) {
    runtime.register({
        my_custom_function: function (color) {
            return this.lighten(color, new Fashion.Numeric(50, '%'));
        },

        my_other_custom_function: function(color1, color2) {
            return this.mix(color1, color2, new Fashion.Numeric(25, '%'));
        }
    });

    runtime.registerSelectorHooks({

        ".theCustomSelector": function(ruleset) {
            ruleset.addDeclaration(new Fashion.Declaration({
                property: "background-image",
                value: new Fashion.List([
                    new Fashion.Text('the custom value', '"')
                ])
            }));
        }

    });

    runtime.registerStyleHooks({

        "the-custom-style-name": function(decl) {
            decl.value = new Fashion.List([
                new Fashion.Text('Hello', '"'),
                new Fashion.Text('World', "'")
            ]);
        }

    });
};
