var Fashion = require('../../index.js');
var assert = require('assert');
var fs = require('fs');
var Path = require('path');

describe("css3", function(){


    it("should pass through standard css3 rules", function(done){
        var builder = new Fashion.Builder({
                compressed: true
            }),
            path = '../files/css3/css3.scss',
            expectedPath = '../files/css3/css3.css';

        builder.build(Path.resolve(__dirname, path), function(content){
            var expected = fs.readFileSync(Path.resolve(__dirname, expectedPath), 'utf-8');
            assert.equal(content.join(''), expected);
            done();
        });
    });
});