var assert = require('assert');
var helpers = require('../helpers.js');

describe('runtime', function(){

    describe('function calls', function() {
        helpers.xtest('should pass through var args dispatch', [
            '@mixin foo($a, $b) {',
            '    a: $a $b;',
            '}',
            '@mixin bar($args...) {',
            '    c: d;',
            '    @include foo($args...);',
            '}',
            '.a {',
            '    @include bar($b: foo, $a: bar);',
            '}'
        ], [
            '.a {',
            '    c: d;',
            '    a: bar foo;',
            '}'
        ]);
    });

});